class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def get_min_k(num_index):
            infinity = len(queries) + 1

            @cache
            def dp(value, i):
                if value == 0:
                    return i

                if i == len(queries):
                    return infinity

                result = dp(value, i + 1)
                l, r, val = queries[i]
                if l <= num_index <= r and val <= value:
                    result = min(result, dp(value - val, i + 1))

                return result

            min_k = dp(nums[num_index], 0)
            return min_k if min_k != infinity else -1

        max_index = 0

        for i in range(len(nums)):
            min_index = get_min_k(i)
            if min_index == -1:
                return -1
            max_index = max(max_index, min_index)

        return max_index

