class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int n = nums.size();
        const int q = queries.size();
        if (*max_element(nums.begin(), nums.end()) == 0) return 0;
        vector<vector<bool>> v(n);
        for (int i = 0; i < n; ++i) {
            v[i].resize(nums[i] + 1);
            v[i][0] = true;
        }
        for (int i = 0; i < q; ++i) {
            for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                for (int k = nums[j]; k >= queries[i][2]; --k) {
                    v[j][k] = (v[j][k] || v[j][k - queries[i][2]]);
                }
            }
            bool ok = true;
            for (int i = 0; i < n; ++i) {
                if (!v[i][nums[i]]) {
                    ok = false;
                    break;
                }
            }
            if (ok) return i + 1;
        }
        return -1;
    }
};

