class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all([num == 0 for num in nums]):
            return 0
        if not self.works(nums, queries, len(queries)):
            return -1
        low_guess = 1
        high_guess = len(queries)
        while low_guess < high_guess:
            mid_guess = (low_guess + high_guess) // 2
            if self.works(nums, queries, mid_guess):
                high_guess = mid_guess
            else:
                low_guess = mid_guess + 1
        return low_guess

    def works(self, nums, queries, guess):
        #print("")
        #print("procing guess {}".format(guess))
        n = len(nums)
        counts = [
            [
                0 for j in range(0, 11)
            ] for i in range(0, n)
        ]
        for k in range(0, guess):
            l, r, v = queries[k]
            for i in range(l, r+1):
                counts[i][v] += 1
        #for row in counts:
        #    print(row)
        for i in range(0, n):
            reached = [False] * (nums[i] + 1)
            reached[0] = True
            for v in range(0, 11):
                for c in range(0, counts[i][v]):
                    for j in range(nums[i], -1, -1):
                        if reached[j] and j + v <= nums[i]:
                            reached[j+v] = True
            #print(reached)
            if not reached[-1]:
                return False
        return True

