class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        c = [[] for _ in range(n)]
        for j, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                c[i].append((j, v))
        r = [0] * n
        for i in range(n):
            if nums[i] == 0:
                r[i] = 0
                continue
            cc = sorted(c[i], key=lambda x: x[0])
            target = nums[i]
            dp = [False] * (target + 1)
            dp[0] = True
            found = None
            for (j, v) in cc:
                new_dp = dp[:]
                for s in range(target - v, -1, -1):
                    if dp[s]:
                        new_dp[s + v] = True
                dp = new_dp
                if dp[target]:
                    found = j + 1
                    break
            if not dp[target]:
                return -1
            r[i] = found
        return max(r)

