int dp[N][N];

int solve(int val, int idx, vector<vector<int>>& q, int id) {
    if(val < 0) {
        return INF;
    }
    if(val == 0) {
        return idx;
    }
    if(idx == q.size()) {
        return INF;
    }
    int &res = dp[val][idx];
    if(res != -1) return res;
    res = solve(val, idx + 1, q, id);
    if(id >= q[idx][0] && id <= q[idx][1]) {
        res = min(res, solve(val - q[idx][2], idx + 1, q, id));
    }
    return res;
}

