public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        rep(i,0,nums.size()) {
            memset(dp, -1, sizeof(dp));
            int x = solve(nums[i], 0, queries, i);
            if(x == INF) return -1;
            ans = max(ans, x);
        }
        return ans;
    }
};

