class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(x == 0 for x in nums):
            return 0

        lo, hi = 1, len(queries) + 1  
        ans = -1

        while lo < hi:
            mid = (lo + hi) // 2
            if self.can_zero(nums, queries[:mid]):
                ans = mid
                hi = mid
            else:
                lo = mid + 1

        return ans if ans != -1 else -1

    def can_zero(self, nums: List[int], queries: List[List[int]]) -> bool:

        n = len(nums)
        for i in range(n):
            target = nums[i]
            available = [val for l, r, val in queries if l <= i <= r]
            if not self.subset_sum(available, target):
                return False
        return True

    def subset_sum(self, arr: List[int], target: int) -> bool:

        dp = 1  
        for x in arr:
            dp |= dp << x
        return (dp >> target) & 1 == 1

