class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        nq = len(queries)
        dec = [[0] * nq for _ in range(n)]
        for i, query in enumerate(queries):
            l, r, val = query
            for j in range(l, r+1):
                dec[j][i] = val
        max_k = 0
        for i in range(n):
            max_k = max(max_k, self.find_min(dec[i], nums[i]))
        return max_k if max_k != nq + 1 else -1

    def find_min(self, array, target) -> int:
        if target == 0:
            return 0
        prev = set()
        prev.add(0)
        for i, num in enumerate(array):

            temp = set()
            for p in prev:
                temp.add(p)
                temp.add(p+num)
            if target in temp:
                return i + 1
            prev = temp
        return len(array) + 1

