class Solution {

    public int minZeroArray(int[] nums, int[][] queries) {
        int max = -1;
        for (int i = 0; i < nums.length; i++) {
            max = Math.max(max, minZeroArray(i, nums[i], queries));
        }
        return max == Integer.MAX_VALUE ? -1 : max;
    }

    private int minZeroArray(int i, int num, int[][] queries) {
        if (num == 0) {
            return 0;
        }
        boolean[] dp = new boolean[1010];
        dp[0] = true;
        for (int j = 0; j < queries.length; j++) {
            if (i >= queries[j][0] && i <= queries[j][1]) {
                for (int k = 1009; k >= queries[j][2]; k--) {
                    dp[k] |= dp[k - queries[j][2]];
                }
                if (dp[num]) {
                    return j + 1;
                }
            }
        }
        return Integer.MAX_VALUE;
    }
}

