        int l = 0, r = q;
        while (l < r) {
            int m = (l + r - 1) / 2;
            //cout << m << endl;

            bool good = 1;
            for (int i = 0; i < n && good; i++) {
                vector<bool> dp(1005);
                dp[0] = 1;
                for (int j = 0; j < m; j++) {
                    auto v = queries[j];
                    if (i >= v[0] && i <= v[1]) {
                        for (int k = 1000 - dp[2]; k >= 0; k--)
                            if (dp[k])
                                dp[k + v[2]] = 1;
                    }
                }
                //cout << m << "    " << dp[nums[i]] << endl;
                good &= dp[nums[i]];
            }
            if (good) r = m;
            else l = m + 1;
        }
        if (l == q) {
            int m = q;
            bool good = 1;
            for (int i = 0; i < n && good; i++) {
                vector<bool> dp(1005);
                dp[0] = 1;
                for (int j = 0; j < m; j++) {
                    auto v = queries[j];
                    if (i >= v[0] && i <= v[1]) {
                        for (int k = 1000 - dp[2]; k >= 0; k--)
