constexpr int MAXN = 16;
constexpr int MAXM = 1024;

int a[MAXN], l[MAXM], r[MAXM], w[MAXM], dp[MAXM];

bool CheckCol(int n, int k) {
  int m = a[k];
  fill(dp, dp + m + 1, 0);
  dp[0] = 1;
  for (int i = 0; i < n; ++i) {
    if (k < l[i] || k > r[i]) {
      continue;
    }
    for (int j = m; j >= w[i]; --j) {
      dp[j] |= dp[j - w[i]];
    }
  }
  return dp[m];
}

bool Check(int n, int m) {
  for (int i = 0; i < n; ++i) {
    if (!CheckCol(m, i)) {
      return false;
    }
  }
  return true;
}

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
      int n = nums.size(), m = queries.size();
      copy(nums.begin(), nums.end(), a);
      for (int i = 0; i < m; ++i) {
        l[i] = queries[i][0];
        r[i] = queries[i][1];
        w[i] = queries[i][2];
      }
      int l = 0, r = m + 1, ret = -1;
      while (l < r) {
        int mid = l + (r - l) / 2;
        if (Check(n, mid)) {
          ret = mid;
          r = mid;
        } else {
          l = mid + 1;
        }
      }
      return ret;
    }
};

