            int l = queries[q][0], r = queries[q][1], val = queries[q][2];
            fornn(i, l, r+1) bs[i] |= bs[i] << val;
            ok = 1;
            forn(i, n) if(!bs[i].test(a[i])) ok = 0;
            if(ok) return q+1;
        }
        return -1;
    }
};

