class Solution {
public:
    bool ispossible(vector<int>& nums, vector<vector<int>>& queries,int q)
    {
        int n=nums.size();
        vector<vector<int>> vp(n);
        for(int i=0;i<q;i++)
            {
                for(int j=queries[i][0];j<=queries[i][1];j++)
                    {
                        vp[j].push_back(queries[i][2]);
                    }
            }
        for(int i=0;i<nums.size();i++)
            {
                if(nums[i]==0) continue;
                if(vp[i].size()==0) return 0;
                int sz=vp[i].size();
                vector<vector<int>> dp(sz+1,vector<int> (nums[i]+1,0));
                 for(int j=0;j<=sz;j++) dp[j][0]=1;
                int target=nums[i];
                vector<int> arr;
                for(auto j : vp[i]) arr.push_back(j);
                for(int ii=1;ii<=sz;ii++)
                {
            for(int j=1;j<=target;j++)
            {
                dp[ii][j]=dp[ii-1][j];
                if(j>=arr[ii-1])
                dp[ii][j]|=dp[ii-1][j-arr[ii-1]];
            }

        }
                  if(dp[sz][target]==0) return 0;
            }


        return 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q=queries.size();
        bool f=1;
        for(auto j : nums) f=f&(j==0);
        if(f) return 0;
        int st=1,end=q;
        int ans=-1;
        while(st<=end)
            {
                int mid=(st+end)/2;
                if(ispossible(nums,queries,mid))
                {
                    ans=mid;
                    end=mid-1;
                }
                else
                    st=mid+1;
            }
        return ans;
    }
};

