class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        @lru_cache(None)
        def check(v, count):
            if v==0: return True
            if len(count)<=1 or v<0: return False
            x = len(count)-1
            for mul in range(count[x], -1, -1):
                if check(v - mul*x, count[:x]): return True
            return False

        def feasible(x):
            data=[[0]*11 for _ in range(n)]
            for l,r,v in queries[:x]:
                for i in range(l,r+1):
                    data[i][v] += 1
            # print(x, nums, data)
            return all(check(v, tuple(d)) for v,d in zip(nums,data))
        l,r,res = 0, len(queries), -1
        while l<=r:
            mid=(l+r)//2
            if feasible(mid): res, r = mid, mid-1
            else: l=mid+1
        return res

