int minZeroArray(int* nums, int numsSize, int** queries, int queriesSize, int* queriesColSize) {

    int all_zero = 1;
    for (int i = 0; i < numsSize; i++) {
        if (nums[i] != 0) {
            all_zero = 0;
            break;
        }
    }
    if (all_zero) {
        return 0;
    }

    int max_k = -1;

    for (int i = 0; i < numsSize; i++) {
        int target = nums[i];
        if (target == 0) {
            continue; 
        }

        int* covering_j = (int*)malloc(queriesSize * sizeof(int));
        int* covering_val = (int*)malloc(queriesSize * sizeof(int));
        int count = 0;

        for (int j = 0; j < queriesSize; j++) {
            int l = queries[j][0];
            int r = queries[j][1];
            if (i >= l && i <= r) {
                covering_j[count] = j;
                covering_val[count] = queries[j][2];
                count++;
            }
        }

        for (int a = 0; a < count; a++) {
            for (int b = a + 1; b < count; b++) {
                if (covering_j[a] > covering_j[b]) {
                    int temp_j = covering_j[a];
                    int temp_val = covering_val[a];
                    covering_j[a] = covering_j[b];
                    covering_val[a] = covering_val[b];
                    covering_j[b] = temp_j;
                    covering_val[b] = temp_val;
                }
            }
        }

        int* dp = (int*)calloc(target + 1, sizeof(int));
        dp[0] = 1;
        int earliest_j = -1;

        for (int q = 0; q < count; q++) {
            int val = covering_val[q];
            int j = covering_j[q];

            for (int s = target; s >= val; s--) {
                if (dp[s - val] && !dp[s]) {
                    dp[s] = 1;
                    if (s == target && earliest_j == -1) {
                        earliest_j = j;
                    }
                }
            }

            if (earliest_j != -1) {
                break;
            }
        }

        free(covering_j);
        free(covering_val);

        if (!dp[target]) {
            free(dp);
            return -1;
        }

        if (earliest_j > max_k) {
            max_k = earliest_j;
        }

        free(dp);
    }

    if (max_k == -1) {
        return 0;
    }

    return max_k + 1;
}

