function minZeroArray(nums: number[], queries: number[][]): number {
    const n = nums.length;
    if (n === 0) return -1;
    if (nums.every(x => x === 0)) return 0;

    const maxVal = 1000;
    const dp: boolean[][] = [];

    for (let i = 0; i < n; i++) {
        dp[i] = new Array(maxVal + 1).fill(false);
        dp[i][0] = true;
    }

    for (let k = 1; k <= queries.length; k++) {
        const [l, r, val] = queries[k - 1];

        for (let i = l; i <= r; i++) {
            for (let s = maxVal; s >= val; s--) {
                if (dp[i][s - val]) dp[i][s] = true;
            }
        }

        let allZero = true;
        for (let i = 0; i < n; i++) {
            if (nums[i] <= maxVal && !dp[i][nums[i]]) {
                allZero = false;
                break;
            }
        }

        if (allZero) return k;
    }

    return -1;
}

