            int index = 1;
            for(vector<int> q: queries){
                if(q[0] <= i && q[1] >= i){
                    set<int> ss;
                    for(int x: s){
                        ss.insert(x);
                        if(x + q[2] > nums[i])
                            continue;
                        ss.insert(x+q[2]);
                    }
                    s = ss;
                }
                if(s.find(nums[i]) != s.end()){
                    mini = max(mini, index);
                    break;
                }
                index++;
            }
            if(index == queries.size() + 1)
                return -1;
        }
        return mini;
    }
};

