class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& Q) {
        int n = a.size(), q = Q.size();
        int sum = 0;

        for(int i=0; i<n; i++) sum += a[i];
        if(sum == 0) return 0;

        vector<bitset<1001>> b(n);
        for(int i=0; i<n; i++) b[i].set(a[i]);

        for(int i=0; i<q; i++)
        {
            int l = Q[i][0], r = Q[i][1], v = Q[i][2];
            sum = 0;

            for(int j=l; j<=r; j++)
                {
                    b[j] |= b[j] >> v;
                }

            for(int i = 0; i < n; i++) sum += b[i][0];
            if(sum == n) return i + 1;
        }

        return -1;
    }
};

