class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int pos = 0;
        for (int i = 0; i < n; i++) {
            int x = nums[i];
            vector<bool> d(x + 1, false);
            d[0] = true;
            int j = 0;
            while (j < m && d[x] == false) {
                int l = queries[j][0];
                int r = queries[j][1];
                int v = queries[j][2];
                if (i >= l && i <= r) {
                    for (int k = x; k >= v; k--) {
                        if (d[k - v] == true) d[k] = true;
                    }
                }
                j++;
            }
            if (d[x] == false) return -1;
            if (j > pos) pos = j;
        }
        return pos;
    }
};

