const int M = 1001;
class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), m = q.size();

        bool ok = true;
        for (int i = 0; i < n; i++){
            if (nums[i] != 0) ok = false;
        }
        if (ok) return 0;

        vector<bitset<M>> bits(n);
        for (int i = 0; i < n; i++){
            bits[i][0] = 1;
        }
        vector<int> best(n, m + 1);

        for (int i = 0; i < m; i++){
            int l = q[i][0], r = q[i][1], v = q[i][2];
            for (int j = l; j <= r; j++){
                bits[j] |= (bits[j] << v);
                if (bits[j][nums[j]] == 1) best[j] = min(best[j], i + 1);
            }
        }
        auto x = *max_element(best.begin(), best.end());
        if (x == m + 1) return -1;
        return x;

    }
};

