class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int ans = 0;
        for (int i = 0; i < n; i++) {
            boolean[] dp = new boolean[nums[i] + 1];
            dp[0] = true;

            for (int q = 0; q < queries.length; q++) {
                int l = queries[q][0], r = queries[q][1], v = queries[q][2];

                if (dp[nums[i]]) {
                    ans = Math.max(ans, q);
                    break;
                }

                if (l <= i && i <= r) {
                    for (int j = nums[i]; j >= v; j--)
                        dp[j] |= dp[j - v];
                }

                if (dp[nums[i]]) {
                    ans = Math.max(ans, q + 1);
                    break;
                }
            }

            if (!dp[nums[i]])
                return -1;
        }

        return ans;
    }
}

