class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<pair<int, int>>> for_j(n); 

        for (int i = 0; i < m; ++i) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; ++j) {
                for_j[j].emplace_back(val, i);
            }
        }

        int max_k = 0;

        for (int j = 0; j < n; ++j) {
            int target = nums[j];
            if (target == 0) continue;

            vector<int> dp(target + 1, m); 
            dp[0] = -1;

            for (const auto& [val, idx] : for_j[j]) {
                vector<int> temp = dp;
                for (int s = 0; s <= target; ++s) {
                    if (temp[s] <= m) {
                        int new_sum = s + val;
                        if (new_sum > target) continue;
                        int new_max = max(temp[s], idx);
                        if (new_max < dp[new_sum]) {
                            dp[new_sum] = new_max;
                        }
                    }
                }
            }
            if (dp[target] == m) {
                return -1;
            }
            max_k = max(max_k, dp[target] + 1);
        }
        return (max_k <= m) ? max_k : -1;
    }
};

