        const int inf = 1e9;

        auto f = [&] (int i) {
            int x = a[i];
            vi dp(x+1);
            dp[0] = 1;
            int ret = inf;
            if (dp[x]) return ret = 0;
            for (int j=0; j<q; j++) {
                int l = qr[j][0], r = qr[j][1], w = qr[j][2];
                if (l <= i && i <= r) {
                    for (int l=x; l>=0; l--) {
                        if (l+w <= x && dp[l]) {
                            dp[l+w] = 1;
                        }
                    }
                }
                if (dp[x]) return ret = j+1;
            }
            return ret;
        };

        int ans = 0, n = a.size();
        for (int i=0; i<n; i++) {
            ans = max(ans, f(i));
        }
        if (ans == inf) ans = -1;
        return ans;
    }
};

