                if (!dp[i][nums[i]])
                    return false;
            return true;
        };
        if (allZero()) return 0;
        for (int q = 0; q < queries.size(); q++) {
            int l = queries[q][0], r = queries[q][1], v = queries[q][2];
            for (int i = l; i <= r; i++)
                if (nums[i] >= v)
                    updateDP(dp[i], nums[i], v);
            if (allZero()) return q + 1;
        }
        return -1;
    }
};

