                for (int j = nums[i]; j >= w; j--) {
                    dp[i][j] = dp[i][j] || dp[i][j - w];
                }
            }

            bool can = true;

            for (int i = 0; i < n; i++) {
                if (!dp[i].back()) {
                    can = false;
                    break;
                }
            }

            if (can) {
                return cnt;
            }
        }

        return -1;
    }
};

