class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    // If nums is already a Zero Array, return 0.
    bool allZero = true;
    for (int x : nums) {
        if (x != 0) { allZero = false; break; }
    }
    if(allZero) return 0;

    int q = queries.size();
    int lo = 1, hi = q, ans = -1;

    while(lo <= hi) {
        int mid = lo + (hi - lo) / 2;
        bool feasible = true;
        // For each index, check if we can achieve exactly nums[i] using queries[0..mid-1]
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            // Build the list of available values for index i.
            vector<int> vals;
            for (int j = 0; j < mid; j++) {
                if (queries[j][0] <= i && i <= queries[j][1])
                    vals.push_back(queries[j][2]);
            }
            // Subset-sum DP (using reverse iteration to avoid reuse in same query)
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int v : vals) {
                for (int s = target; s >= v; s--) {
                    if (dp[s - v]) dp[s] = true;
                }
            }
            if (!dp[target]) { 
                feasible = false; 
                break; 
            }
        }
        if(feasible) {
            ans = mid;
            hi = mid - 1;
        } else {
            lo = mid + 1;
        }
    }
    return ans;
}
};

