class Solution {
public:
    bool canTransformToZeroArray(int numberOfProcessedQueries, const vector<int>& originalArray, const vector<vector<int>>& queryList) {
        int arraySize = originalArray.size();
        for (int j = 0; j < arraySize; j++) {
            if (originalArray[j] == 0) continue;
            bitset<1001> memo;
            memo.reset();
            memo[0] = 1;
            for (int i = 0; i < numberOfProcessedQueries; i++) {
                int leftBoundary = queryList[i][0];
                int rightBoundary = queryList[i][1];
                int decrementAmount = queryList[i][2];
                if (j >= leftBoundary && j <= rightBoundary) {
                    memo |= (memo << decrementAmount);
                }
            }
            if (!memo.test(originalArray[j])) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int totalQueries = queries.size();
        bool isAlreadyZero = true;
        for (int value : nums) {
            if (value != 0) { isAlreadyZero = false; break; }
        }
        if (isAlreadyZero) return 0;

        int low = 0, high = totalQueries, minimumQueriesNeeded = -1;
        while (low <= high) {
            int midPoint = (low + high) / 2;
            if (canTransformToZeroArray(midPoint, nums, queries)) {
                minimumQueriesNeeded = midPoint;
                high = midPoint - 1;
            } else {
                low = midPoint + 1;
            }
        }
        return minimumQueriesNeeded;
    }
};

