                        int v = queries[j][2];
                        for (int s = curr - v; s >= 0; s--) {
                            if (dp[s]) dp[s + v] = 1;
                        }
                    }
                }
                if (!dp[curr]) { b = false; break; }
            }
            if (b) return k;
        }
        return -1;
    }
};

