class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        bool allZero = true;
        for (int x : nums) {
            if (x != 0) { allZero = false; break; }
        }
        if (allZero) return 0;
        vector<set<int>> dp(n);
        for (int i = 0; i < n; i++) dp[i].insert(0);
        int k = 0;
        for (auto &query : queries) {
            k++;
            int l = query[0], r = query[1], v = query[2];
            for (int i = l; i <= r; i++) {
                set<int> newSums;
                for (int sum : dp[i]) {
                    if (sum + v <= nums[i]) newSums.insert(sum + v);
                }
                dp[i].insert(newSums.begin(), newSums.end());
            }
            bool possible = true;
            for (int i = 0; i < n; i++) {
                if (dp[i].find(nums[i]) == dp[i].end()) { possible = false; break; }
            }
            if (possible) return k;
        }
        return -1;
    }
};

