class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        m = len(nums)
        dp = [set([nums[i]]) for i in range(m)]
        checked = 0
        for i in range(m):
            if nums[i] == 0:
                dp[i] = None
                checked += 1
        if checked == m:
            return 0
        for i, (l,r,x) in enumerate(queries):
            for j in range(l, r+1):
                if dp[j] is None:
                    continue
                dp[j].update([t-x for t in dp[j] if t >= x])
                if 0 in dp[j]:
                    checked += 1
                    dp[j] = None
            # print(dp)
            if checked == m:
                return i + 1
        return -1

