        for (int i: nums) sum += i;
        if (sum == 0) return 0;

        boolean [][] knap = new boolean[nums.length][1001];
        for (int i = 0; i < nums.length; i++) knap[i][0] = true;
        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                for (int k = nums[j]; k >= 0; k--) {
                    if (k - val >= 0) knap[j][k] |= knap[j][k - val];
                }
            }
            boolean works = true;
            for (int j = 0; j < nums.length; j++) {
                if (!knap[j][nums[j]]) works = false;
            } if (works) return i + 1;
        } return -1;
    }
}

