class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        def solve(mid):
            maxs = 1100
            for i in range(n):
                dp = [False] * maxs
                dp[0] = True
                for k in range(mid):
                    l, r, val = queries[k]
                    if l <= i <= r:
                        for s in range(maxs - 1, val - 1, -1):
                            if dp[s - val]:
                                dp[s] = True
                if not dp[nums[i]]:
                    return False
            return True

        lo, hi = 0, m + 1
        while lo < hi:
            mid = lo + (hi - lo) // 2
            if solve(mid):
                hi = mid
            else:
                lo = mid + 1

        return lo if lo <= m else -1

