class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n=nums.size(), m=q.size();
        auto flag = [&](int k) -> bool {
            for (int i=0; i<n; i++) {
                bitset<1100> dp;
                dp.reset();
                dp[0] = 1;
                for(int j=0; j<k; j++){
                    int l = q[j][0], r = q[j][1],
                        val = q[j][2];
                    if(i >= l && i <= r)
                        dp |= (dp << val);
                }
                if (!dp.test(nums[i]))
                    return false;
            }
            return true;
        };
        int lo = 0, hi = m, ans = -1;
        while (lo <= hi) {
            int mid = (lo + hi) / 2;
            if(flag(mid)){
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

