class Solution {
public:

    vector<vector<int>> qu;
    int q;

    vector<vector<vector<int>>> dp;
    int func(int idx, int v, int q_idx) {
        if(v < 0) return 0;
        if(q_idx == -1) return v == 0;

        if(~dp[idx][v][q_idx]) return dp[idx][v][q_idx];
        int ch1 = func(idx, v, q_idx - 1);
        int ch2 = 0;
        if(idx >= qu[q_idx][0] && idx <= qu[q_idx][1]) {
            ch2 = func(idx, v - qu[q_idx][2], q_idx - 1);
        }
        return dp[idx][v][q_idx] = (ch1 || ch2);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        qu = queries;
        q = qu.size();

        int mx = *max_element(nums.begin(), nums.end());
        dp.assign(nums.size(), vector<vector<int>>(mx + 1, vector<int>(queries.size(), -1)));
        int ans = -1;
        for (int i = 0; i < nums.size(); ++i) {
            if(!func(i, nums[i], (int)qu.size() - 1)) return -1;
            for (int j = -1; j < (int)qu.size(); ++j) {
                if(func(i, nums[i], j)) {
                    ans = max(ans, j + 1);
                    break;
                }
            }
        }

        return ans;
    }

};

