                if (i >= queries[j][0] && i <= queries[j][1])
                    for (int k = nums[i]; k >= queries[j][2]; k--)
                        vis[k] = (vis[k] || vis[k-queries[j][2]]);
                if (vis[nums[i]]) {
                    val[i] = j+1;
                    break;
                }
            }
        }
        int ans = 0;
        for (int x : val)
            ans = max(ans, x);
        return (ans == q+1) ? -1 : ans;
    }
};

