        if all(x == 0 for x in nums):
            return 0

        def good(k):
            for i in range(n):
                mask = 1
                for l, r, val in queries[:k]:
                    if l <= i <= r:
                        mask |= (mask << val)
                        if (mask & (1 << nums[i])):
                            break
                if (mask & (1 << nums[i])) == 0:
                    return False

            return True

        l, r = 1, 10 ** 10
        while l < r:
            mid = l + (r - l) // 2
            if good(mid):
                r = mid
            else:
                l = mid + 1

        return l if good(l) else -1

