class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int outstanding = nums.size();
        vector<vector<bool>> memo(nums.size(), vector<bool>(1001, false));
        for (int i = 0; i < nums.size(); i++) {
            memo[i][nums[i]] = true;
            if (!nums[i]) outstanding--;
        }
        if (outstanding == 0) return 0;

        int n = queries.size();
        for (int k = 0; k < n; k++) {
            int l = queries[k][0], r = queries[k][1], val = queries[k][2];
            for (int i = l; i <= r; i++) {
                if (memo[i][0]) continue;
                for (int j = val; j <= nums[i]; j++) {
                    if (memo[i][j]) memo[i][j-val] = true;
                }
                if (memo[i][0]) {
                    outstanding--;
                    if (!outstanding) return k+1;
                }
            }
        }

        return -1;
    }
};

