class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),k=queries.size(),id=0;
        vector<vector<int>>dp(1001,vector<int>(1001));
        vector<vector<int>>vis(1001,vector<int>(1001));
        auto rec=[&](auto &self,int i,int rem,int idx)->int{
            if(rem==0)return i;
            if(rem<0)return 1e6;
            if(i==k)return 1e6;
            int &ret=dp[i][rem];
            if(vis[i][rem]==id)return ret;
            vis[i][rem]=id;
            ret=1e6;
            int l=queries[i][0],r=queries[i][1], x=queries[i][2];
            if(idx<l || idx>r)x=0;
            ret=min(ret,self(self,i+1,rem-x,idx));
            ret=min(ret,self(self,i+1,rem,idx));
            return ret;
        };
        int mx=0;
        for (int i = 0; i < n; ++i) {
            id++;
            mx=max(mx,rec(rec,0,nums[i],i));
            if(mx>k)break;
        }
        if(mx>k)return -1;
        return mx;
    }
};

