        if max(nums) == 0:
            return 0
        n = len(nums)
        dp = [[0] * (x + 1) for x in nums]
        for i in range(n):
            dp[i][0] = 1

        k = 0
        for l, r, v in queries:
            k += 1
            while l <= r:
                for t in range(nums[l], v - 1, -1):
                    dp[l][t] |= dp[l][t - v]
                l += 1
            ok = 1
            for i in range(n):
                if dp[i][-1] != 1:
                    ok = 0
            if ok == 1:
                return k
        return -1

