#include <stdlib.h>
#include <stdbool.h>
#include <string.h>

int minZeroArray(int* nums, int numsSize, int** queries, int queriesSize, int* queriesColSize) {
    bool isZero = true;
    for (int i = 0; i < numsSize; i++) {
        if (nums[i] != 0) { isZero = false; break; }
    }
    if (isZero) return 0;
    int lo = 1, hi = queriesSize, ans = -1;
    while (lo <= hi) {
        int mid = (lo + hi) / 2;
        bool possible = true;
        for (int i = 0; i < numsSize; i++) {
            int target = nums[i];
            int dpSize = target + 1;
            bool* possibleSum = (bool*)calloc(dpSize, sizeof(bool));
            possibleSum[0] = true;
            for (int q = 0; q < mid; q++) {
                if (i >= queries[q][0] && i <= queries[q][1]) {
                    int dec = queries[q][2];
                    for (int s = target; s >= dec; s--) {
                        if (possibleSum[s - dec]) possibleSum[s] = true;
                    }
                }
            }
            if (!possibleSum[target]) { possible = false; }
            free(possibleSum);
            if (!possible) break;
        }
        if (possible) { ans = mid; hi = mid - 1; } else { lo = mid + 1; }
    }
    return ans;
}

