        int i = 0;
        while(i < arr.length) {
            int x = arr[i];
            int j = t;
            while(j >= x) {
                if(dp[j - x]) {
                    dp[j] = true;
                }
                j--;
            }
            i++;
        }
        return dp[t];
    }
}

class QueryChecker {
    public boolean check(int[] a, int[][] q, int k) {
        int n = a.length;
        ok sss = new ok();
        int i = 0;
        while(i < n) {
            ArrayList<Integer> al = new ArrayList<>();
            int j = 0;
            while(j < k && j < q.length) {
                int l = q[j][0];
                int r = q[j][1];
                int v = q[j][2];
                if(i >= l && i <= r) {
                    al.add(v);
                }
                j++;
            }
            int sz = al.size();
            int[] arr = new int[sz];
            int t = 0;
            while(t < sz) {
                arr[t] = al.get(t);
                t++;
            }
            if(!sss.canSum(a[i], arr)) return false;
            i++;
        }
        return true;
    }
}

class ZeroArrayChecker {
    public boolean isZero(int[] a) {
        int i = 0;
        while(i < a.length) {
            if(a[i] != 0) return false;
            i++;
        }
        return true;
    }
}

class BinarySearchSolver {
    public int search(int[] a, int[][] q) {
        int l = 0;
        int h = q.length;
        int res = -1;
        QueryChecker qc = new QueryChecker();
        while(l <= h) {
            int m = (l + h) / 2;
            if(qc.check(a, q, m)) {
                res = m;
                h = m - 1;
            } else {
                l = m + 1;
            }
        }
        return res;
    }
}

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        ZeroArrayChecker zc = new ZeroArrayChecker();
        if(zc.isZero(nums)) return 0;
        BinarySearchSolver bss = new BinarySearchSolver();
        return bss.search(nums, queries);
    }
}

