using namespace std;

#define loop(i, a, n) for (ll i = (a); i < (n); ++i)
#define loopD(i, a, n) for (ll i = (a); i >= (n); --i)
#define sz(a) ((int)a.size())
#define pb push_back

typedef long long ll;
typedef vector<int> vi;
typedef vector<vi> vvi;

class Solution {
public:
    bool isPossible(int idx, int k, vi& arr, vvi& ops) {
        int tgt = arr[idx];
        vector<bool> dp(tgt + 1, false);
        dp[0] = true;

        loop(j, 0, k) {
            int l = ops[j][0], r = ops[j][1], val = ops[j][2];
            if (idx >= l && idx <= r) {
                loopD(s, tgt, val) {
                    dp[s] = dp[s] || dp[s - val];
                }
            }
        }
        return dp[tgt];
    }

    bool solve(int k, vi& arr, vvi& ops) {
        loop(i, 0, sz(arr)) {
            if (!isPossible(i, k, arr, ops))
                return false;
        }
        return true;
    }

    int minZeroArray(vi& arr, vvi& ops) {
        int low = 0, high = sz(ops) + 1;

        while (low < high) {
            int mid = low + (high - low) / 2;
            if (solve(mid, arr, ops))
                high = mid;
            else
                low = mid + 1;
        }
        return (low == sz(ops) + 1) ? -1 : low;
    }
};

