class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        if all(x == 0 for x in nums):
            return 0

        def subset_sum(goal, nums):
            dp = [False] * (goal + 1)
            dp[0] = True
            for n in nums:
                if n > goal:
                    continue
                for i in range(goal, n-1, -1):
                    dp[i] |= dp[i-n]
            return dp[goal]
        def check(limit):
            options = defaultdict(list)

            for j in range(limit+1):
                l,r,v = queries[j]
                for i in range(l, r+1):
                    options[i].append(v)

            return all(subset_sum(n, options[i]) for i,n in enumerate(nums))

        left = 0
        right = len(queries)-1
        while left <= right:
            mid = (left+right)//2

            if check(mid):
                right = mid - 1
            else:
                left = mid + 1
        if left == len(queries):
            left = -1
        return left + 1 if left != -1 else left

