class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> res(nums.size(), -1);
        for (int i = 0; i < nums.size(); i++) {
            int target = nums[i];
            if (target == 0) {
                res[i] = 0;
                continue;
            }

            vector<int> dp(target+1);
            dp[0] = 1;
            int cnt = 1;
            for (auto & q : queries) {
                if (q[0] <= i && i <= q[1]) {
                    for (int w = target; w >= q[2]; w--) {
                        dp[w] = dp[w] || dp[w-q[2]];
                    }
                }

                if (dp[target]) {
                    break;
                }
                cnt++;
            }

            if (dp[target])
                res[i] = cnt;
        }

        int ans = 0;
        for (int i = 0; i < res.size(); i++) {
            if (res[i] == -1)
                return -1;

            ans = max(ans, res[i]);
        }
        return ans;
    }
};

