class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
  int n = nums.size();

        auto canMakeZeroArray = [&](int k) -> bool {
            vector<vector<int>> indexVals(n);
            for (int i = 0; i < k; ++i) {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                for (int j = l; j <= r; ++j) {
                    indexVals[j].push_back(val);
                }
            }

            for (int i = 0; i < n; ++i) {
                int target = nums[i];
                if (target == 0) continue;

                // Use subset sum DP with bitset
                bitset<10001> dp;
                dp[0] = 1;
                for (int val : indexVals[i]) {
                    dp |= (dp << val);
                }

                if (!dp[nums[i]]) return false;
            }
            return true;
        };

        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) return 0;

        int left = 1, right = queries.size();
        if (!canMakeZeroArray(right)) return -1;

        while (left < right) {
            int mid = left + (right - left) / 2;
            if (canMakeZeroArray(mid)) right = mid;
            else left = mid + 1;
        }

        return left;
    }
};

