        int have = 0, N = a.size();
        vector dp(N, vector<bool>(1001, false));
        for (int i = 0; i < N; i++) {
            dp[i][a[i]] = 1;
            if (a[i] == 0) have++;
        }
        if (have == N) return 0;
        for (int q = 0; q < queries.size(); q++) {
            int l = queries[q][0], r = queries[q][1], val = queries[q][2];
            for (int i = l; i <= r; i++) {
                if (!dp[i][0] && dp[i][val]) have++;
                for (int j = val; j <= 1000; j++) if (dp[i][j]) dp[i][j - val] = 1;
            }

            if (have == N) return q + 1;
        }
        return -1;
    }
};

