class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n, q = len(nums), len(queries)
        # Treat each number separately: look how many to get it to zero! (binary)
        if all(nums[i] == 0 for i in range(n)):
            return 0

        def mink(i: int) -> int:
            if nums[i] == 0:
                return 0
            vals = {nums[i]}
            for j in range(q):
                l, r, v = queries[j]
                if not l <= i <= r:
                    continue
                newvals = set()
                for val in vals:
                    if val == v:
                        return j
                    if val > v:
                        newvals.add(val-v)
                vals.update(newvals)
            return -1

        ks = [mink(i) for i in range(n)]
        # print("ksks", ks)
        if -1 in ks:
            return -1
        else:
            return max(ks) + 1

