class Solution {
public:
    bool valid(vector<int> &nums,vector<bool> &done,vector<set<int>> &v){
        int n=nums.size();
        for(int i=0;i<n;i++){
            if(nums[i]==0) continue;
            if(done[i]) continue; 
            if(v[i].count(nums[i])) {
                done[i]=true;
                continue;
            }
            return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        vector<bool> done(n,false);
        vector<set<int>> v(n);
        if(valid(nums,done,v)) return 0;
        int sz=queries.size();
        for(int i=0;i<sz;i++){
            int l=queries[i][0];
            int r=queries[i][1];
            int val=queries[i][2];
            for(int k=l;k<=r;k++){
                if(done[k]) {
                    continue;
                }
                vector<int> tmp;
                tmp.push_back(val);
                for(auto i:v[k]){
                    tmp.push_back(i+val);
                }
                while(!tmp.empty()){
                    v[k].insert(tmp.back());
                    tmp.pop_back();
                }
            }
            if(valid(nums,done,v)) return i+1;
        }
        return -1;
    }
};

