class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        int m = q.size();

        auto judge = [&](int k) -> bool {
            rep(i, n){
                bitset<1101> dp;
                dp.reset();
                dp[0] = 1;
                rep(j, k){
                    if (q[j][0] <= i && i <= q[j][1]){
                        dp |= dp << q[j][2];
                    }
                }
                if (!dp.test(a[i])) return false;
            }
            return true;
        };

        int lo = 0, hi = m+1;
        while (lo < hi){
            int mid = lo+(hi-lo)/2;
            if(judge(mid)) hi = mid;
            else lo = mid+1;
        }

        return (lo == m+1 ? -1:lo);
    }
};

