class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        k = -1
        @cache
        def solve(c, p, i):
            if c == 0:
                return 0
            if i>=len(queries):
                return float('Inf')

            if queries[i][0]<=p and queries[i][1]>=p and c>=queries[i][2]:
                return min(1+solve(c-queries[i][2], p, i+1), 1+solve(c, p, i+1))
            return 1+solve(c, p, i+1)

        for i, num in enumerate(nums):
            curr = solve(num, i, 0)
            if curr != float("inf"):
                k = max(k, curr)
            else:
                return -1
        return k

